var dir = flow.get("workingDir");
var host = flow.get("host");
var port = flow.get("port");
var username = flow.get("username");
var password = flow.get("password");

const client = new ftp.Client()
client.ftp.verbose = true;
flow.set("tail", true);
try {
    await client.access({
        host: host,
        port: port,
        user: username,
        password: password
    })
    msg.connection = await("Download Admin Log: Connection Open");
    await client.ensureDir(dir);
    while (flow.get("tail")) {
        var latestLogfileName = flow.get("LatestAdminFile");
        await client.downloadTo("LatestAdmin.log", latestLogfileName);
        msg.payload = await("Downloaded file: " + latestLogfileName);
        node.send(msg);
        await timeout(2000);
    }
}
catch (err) {
    console.log(err)
